<?php require_once '../../function/db_connect.php';function TanggalIndo($date){    $BulanIndo = array("Januari", "Februari", "Maret", "April", "Mei", "Juni", "Juli", "Agustus", "September", "Oktober", "November", "Desember");     $tahun = substr($date, 0, 4);    $bulan = substr($date, 5, 2);    $tgl   = substr($date, 8, 2);     $result = $tgl . " " . $BulanIndo[(int)$bulan-1] . " ". $tahun;            return($result);};$idp=$_GET['idp'];$output = array('data' => array());$sql1 = "select * from sk where ptk_id='$idp' order by tanggal_sk desc";$query1 = $connect->query($sql1);while ($row = $query1->fetch_assoc()) {	$ids=$row['id_sk'];	$actionButton = '<a href="../cetak/cetakSK.php?id='.$ids.'" class="btn btn-info btn-border btn-round btn-sm" type="button" target="_blank"><i class="fa fa-print"></i></a>';		$output['data'][] = array(		TanggalIndo($row['tanggal_sk']),		$row['no_sk'],		$row['jabatan'],		$row['pengangkat'],		$actionButton	);}$connect->close();echo json_encode($output);